/* ==UserStyle==
@name tg gruvbox light
@namespace      github.com/Xedecimal/userstyles
@version      0.0.1
@preprocessor stylus
==/UserStyle== */

@-moz-document domain("web.telegram.org") {
  :root {
    --gruvbox-light-fg0: #282828;
    --gruvbox-light-fg1: #3c3836;
    --gruvbox-light-fg2: #504945;
    --gruvbox-light-bg: #fbf1c7;
    --gruvbox-light-bg0-h: #f9f5d7;
    --gruvbox-light-bg0-s: #f2e5bc;
    --gruvbox-light-bg1: #ebdbb2;
    --gruvbox-light-bg2: #d5c4a1;
    --gruvbox-light-aqua-6: #689d6a;
    --gruvbox-light-blue-4: #458588;
    --gruvbox-light-blue-12: #076678;
    --gruvbox-light-red-1: #cc241d;
    --gruvbox-light-red-9: #9d0006;
    --gruvbox-light-gray-7: #7c6f64;
    --gruvbox-light-gray-8: #928374;

    --color-background: var(--gruvbox-light-bg) !important;
    --color-chat-hover: var(--gruvbox-light-bg0-h) !important;
    --color-primary: var(--gruvbox-light-blue-4) !important;
    --color-chat-active: var(--gruvbox-light-blue-4) !important;
    --color-text: var(--gruvbox-light-fg0) !important;
    --color-reaction: var(--gruvbox-light-bg) !important;      
    --color-gray: var(--gruvbox-light-bg2) !important;

    --border-radius-default: 0;
    --border-radius-modal: 0;
    --border-radius-default-small: 0;
    --border-radius-default-tiny: 0;
    --border-radius-messages: 0;
    --border-radius-messages-small: 0;
    --border-radius-forum-avatar: 0;

  }  

  html body, body::before, #MiddleColumn, .messages-layout {
      background-image: unset !important;      
      background-color: var(--gruvbox-light-bg0-s) !important;     
  }

  .Reactions button {
      --reaction-background: var(--gruvbox-light-bg0-s) !important;      
      --reaction-text-color: var(--gruvbox-light-blue-12) !important;      
      --reaction-background-hover: var(--gruvbox-light-blue-12) !important;      
  }
  .Reactions button:hover {
      color: var(--gruvbox-light-bg) !important;      
  }
  .Reactions button.chosen {
      --reaction-background: var(--gruvbox-light-blue-4) !important;   
      color: var(--gruvbox-light-bg) !important;         
  }

  .SearchInput, .SearchInput input {
      border-radius: 0;
  }

  .Button, .Avatar {      
      border-radius: 0 !important;
      --radius: 0 !important;
  }

}

