    --gruvbox-light-gray-8: #928374;
  }
  body {
    background-color: var(--gruvbox-light-bg) !important;
  }
  *[aria-label="Home timeline"],
  *[aria-label="Timeline: Trending now"],
  *[aria-label="Who to follow"] {
    background-color: var(--gruvbox-light-bg);
  }
  *:has(*>*>*[aria-label="Home timeline"]),
  *:has(*>*>*[aria-label="Timeline: Trending now"]),
  *:has(*>*>*[aria-label="Who to follow"]) {
    background-color: var(--gruvbox-light-bg);
    border-color: var(--gruvbox-light-bg0-h);
  }
  *[aria-label="Trending"]>div:first-child>div:first-child {
    background-color: var(--gruvbox-light-bg);
  }
  *[aria-label="Home timeline"]>div:nth-child(3) {
    background-color: var(--gruvbox-light-bg);
  }

.theme-light {    
   --gruvbox-bg: #fbf1c7;
   --gruvbox-bg0_h: #f9f5d7;
   --gruvbox-bg0_s: #f2e5bc;
   --gruvbox-bg1: #ebdbb2;
   --gruvbox-bg2: #d5c4a1;
   --gruvbox-bg4: #a89984;
   --gruvbox-fg: #3c3836;
   --gruvbox-blue4: #458688;
   --gruvbox-blue12: #076678;
   --gruvbox-red1: #cc241d;
   --gruvbox-green2: #98971a;
   --gruvbox-aqua6: #689d6a;

   --background-primary: var(--gruvbox-bg) !important;
   --background-secondary: var(--gruvbox-bg) !important;
   --white-500: var(--gruvbox-bg) !important;
   --primary-200: var(--gruvbox-bg0_s) !important;
   --primary-160: var(--gruvbox-bg0_s) !important;   

   --scrollbar-auto-scrollbar-color-thumb: var(--gruvbox-bg2) !important;
   --scrollbar-auto-scrollbar-color-track: var(--gruvbox-bg) !important;

   --brand-500: var(--gruvbox-blue4);
   --brand-experiment-560: var(--gruvbox-blue12);
   --button-danger-background: var(--gruvbox-red1);
   --button-positive-background: var(--gruvbox-aqua6);
}
